window.addEventListener("DOMContentLoaded",()=>{const t=document.querySelector(".nav__menu"),e=document.querySelectorAll(".nav__item"),a=document.querySelector(".hamburger");a.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),t.classList.toggle("nav__menu_active")}),e.forEach(e=>{e.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),t.classList.toggle("nav__menu_active")})})});